-- Shared Stored Procedure - Can be reused across applications\nCREATE PROCEDURE [dbo].[usp_SharedUpdateUser]\n    @UserID INT,\n    @NewEmail NVARCHAR(256)\nAS\nBEGIN\n    UPDATE Users\n    SET Email = @NewEmail\n    WHERE UserID = @UserID;\nEND;\n\n-- Application-Specific Stored Procedure - Specific to App1 needs\nCREATE PROCEDURE [App1].[usp_App1_SendNotification]\n    @UserID INT,\n    @Message NVARCHAR(MAX)\nAS\nBEGIN\n    -- Custom logic for app-specific task, possibly integrating with other systems or using app-specific tables/views\n    INSERT INTO Notifications(UserID, Message)\n    VALUES(@UserID, @Message);\nEND;