BEGIN TRANSACTION;\n\nBEGIN TRY\n    -- Your SQL Statements here\n    INSERT INTO MyTable (Id, Name) VALUES (1, 'Alice');\n    -- Assuming a UNIQUE constraint or PRIMARY KEY on Id can cause a duplicate key error\n    INSERT INTO MyTable (Id, Name) VALUES (1, 'Bob'); -- This will raise a Duplicate Key error\n\n    UPDATE MyTable SET Name = 'Alicia' WHERE Id = 1;\n    DELETE FROM MyTable WHERE Name = 'Alice';\n\n    COMMIT TRANSACTION;\nEND TRY\nBEGIN CATCH\n    -- Handle the exception here\n    PRINT 'An error occurred: ' + ERROR_MESSAGE();\n    \n    -- Rollback transaction if necessary\n    IF @@TRANCOUNT > 0\n        ROLLBACK TRANSACTION;\n\n    -- Optionally, rethrow or handle the error differently\n    -- THROW; -- Uncomment this line to rethrow the caught exception after the catch block.\nEND CATCH;