-- Login Process\n\nDECLARE @ProvidedPasswordHash varbinary(max);\n\n-- Assume @LoginUsername and @LoginUserInputPassword are the submitted values\nSELECT @StoredPasswordHash = password\nFROM Users\nWHERE username = @LoginUsername;\n\nSET @ProvidedPasswordHash = HASHBYTES('SHA2_512', @LoginUserInputPassword + CAST(@UserID AS char(36)));\n\nIF @StoredPasswordHash = @ProvidedPasswordHash\nBEGIN\n    -- Password is correct\nEND\nELSE\nBEGIN\n    -- Password is incorrect\nEND;