-- Registration Process\n\n-- Use NEWID() for UserID\nDECLARE @UserID uniqueidentifier = NEWID();\nDECLARE @PasswordHash varbinary(max);\n\n-- Assume @UserInputPassword is the user's input password\nSET @PasswordHash = HASHBYTES('SHA2_512', @UserInputPassword + CAST(@UserID AS char(36)));\n\nINSERT INTO Users (userid, username, password)\nVALUES (@UserID, @Username, @PasswordHash);