SELECT id, band_name, song_name, album_name, genre \nFROM us_music \nWHERE `genre` = 'punk'\nUNION ALL\nSELECT id, band_name, song_name, album_name, genre \nFROM de_music \nWHERE `genre` = 'punk'\nUNION ALL\nSELECT id, band_name, song_name, album_name, genre \nFROM fr_music \nWHERE `genre` = 'punk'\nUNION ALL\nSELECT id, band_name, song_name, album_name, genre \nFROM uk_music \nWHERE `genre` = 'punk';