CREATE PROCEDURE dbo.dmsInsertTestData @ptestdata VarChar(50)\nAS\n  SET NOCOUNT ON\n\n  BEGIN TRY\n    INSERT INTO dbo.MyTestData (testdata) VALUES (@ptestdata)\n  END TRY\n  BEGIN CATCH\n    -- Handle the error, log if needed, but for this use case, we just ignore it.\n    IF ERROR_NUMBER() = 2627 -- Error 2627 is the primary key violation error number in SQL Server\n      RETURN 0\n    ELSE\n      -- If another error occurred, re-throw it\n      THROW;\n  END CATCH\n\nRETURN 0