CREATE TABLE main_table (\n    id NUMBER PRIMARY KEY,\n    created_date DATE NOT NULL\n)\nPARTITION BY RANGE (created_date) (\n    PARTITION p2019 VALUES LESS THAN (TO_DATE('2020/01/01','YYYY/MM/DD')),\n    PARTITION p2020 VALUES LESS THAN (TO_DATE('2021/01/01','YYYY/MM/DD'))\n);\n\nCREATE TABLE sub_table (\n    id NUMBER,\n    main_id NUMBER REFERENCES main_table(id),\n    additional_column VARCHAR2(50),  -- Additional column for composite partition\n    CONSTRAINT sub_tab_pk PRIMARY KEY (id, additional_column)\n)\nPARTITION BY RANGE (additional_column) -- Example use of another column for partitioning\n(\n    PARTITION addition_1 VALUES LESS THAN ('B'),\n    PARTITION addition_rest VALUES LESS THAN (MAXVALUE)\n);