-- Publisher server setup script\nUSE master;\nGO\nEXEC sp_replicationdboption @dbname = N'YourDatabase', \n    @optname = N'publish',\n    @value = N'true';\nGO\n\n-- Adding the publication\nUSE YourDatabase;\nGO\nEXEC sp_addpublication \n    @publication = N'YourPublicationName', \n    @description = N'Transactional publication of database ''YourDatabase'' to multiple subscribers.',  \n    @sync_type = N'automatic',\n    @repl_freq = N'continuous', \n    @status = N'active';\nGO\n\n-- Adding the article\nEXEC sp_addarticle \n    @publication = N'YourPublicationName', \n    @type = N'logbased', \n    @source_owner = N'dbo', \n    @source_object = N'YourTableName', \n    @destination_table = N'YourTableName', \n    @destination_owner = N'dbo';\nGO\n\n-- Adding the subscription\nUSE master;\nGO\nEXEC sp_addsubscription \n    @publication = N'YourPublicationName', \n    @subscriber = N'SubscriberServerName',\n    @destination_db = N'DestinationDatabase',\n    @sync_type = N'automatic',\n    @article = N'all';\nGO\n\n-- Initializing the subscription (this might require setting up push or pull setup)\nEXEC sp_addpushsubscription_agent\n    @publication = N'YourPublicationName', \n    @subscriber = N'SubscriberServerName',\n    @subscriber_db = N'DestinationDatabase',\n    @job_login = NULL, -- Windows account to run the agent\n    @job_password = NULL;\nGO