-- Assume we have a table `item_status` with columns `item_id` and `is_complete`\n   CREATE TRIGGER update_item_status_trigger\n   AFTER UPDATE OR INSERT ON your_table\n   FOR EACH ROW EXECUTE FUNCTION update_item_status();\n\n   CREATE OR REPLACE FUNCTION update_item_status()\n   RETURNS TRIGGER AS $$\n   BEGIN\n     INSERT INTO item_status (item_id, is_complete)\n     VALUES (NEW.item_id, is_complete(NEW.your_function_params))\n     ON CONFLICT(item_id) DO UPDATE SET is_complete = EXCLUDED.is_complete;\n     RETURN NEW;\n   END;\n   $$ LANGUAGE plpgsql;