-- list of words\nCREATE TABLE word (\n    id    integer      PRIMARY KEY,\n    word  varchar(32)  NOT NULL UNIQUE\n);\n\nINSERT INTO word (id, word) VALUES (1, 'revolve');\nINSERT INTO word (id, word) VALUES (2, 'rotate');\n\n-- M:M link between words with an order constraint\nCREATE TABLE word_link (\n    word1 integer      REFERENCES word(id) NOT NULL,\n    word2 integer      REFERENCES word(id) NOT NULL,\n    PRIMARY KEY (word1, word2),\n    CONSTRAINT unique_word_pair UNIQUE (LEAST(word1, word2), GREATEST(word1, word2))\n);\n\n-- Inserting links\nINSERT INTO word_link(word1, word2) VALUES (LEAST(1, 2), GREATEST(1, 2));