CREATE TABLE Defects (\n    DefectID INT PRIMARY KEY AUTO_INCREMENT,\n    Title VARCHAR(255) NOT NULL,\n    Description TEXT,\n    ArtifactType ENUM('Requirement', 'Design', 'Test', 'Code') NOT NULL,\n    ArtifactID INT,  -- Link to specific requirement/test/design artifact\n    Status ENUM('Open', 'In Progress', 'Resolved', 'Closed') DEFAULT 'Open',\n    Severity ENUM('Critical', 'High', 'Medium', 'Low') NOT NULL,\n    DetectedAtStage ENUM('Requirement Analysis', 'Design Review', 'Code Review', 'Testing') NOT NULL,\n    FoundBy VARCHAR(255),\n    ReportedOn DATE_DEFAULT CURRENT_DATE,\n    AssignedTo VARCHAR(255)\n);\n\nCREATE TABLE Requirements (\n    RequirementID INT PRIMARY KEY AUTO_INCREMENT,\n    RequirementName VARCHAR(255) NOT NULL,\n    Description TEXT\n);\n\n-- Example inserts for Defects related to non-code artifacts like requirements and design\nINSERT INTO Defects (Title, Description, ArtifactType, ArtifactID, Severity, DetectedAtStage, FoundBy)\nVALUES ('Incorrect Data Type', 'The data type specified in the requirement is incorrect.', 'Requirement', 123, 'High', 'Requirement Analysis', 'Team Lead');