CREATE OR REPLACE PROCEDURE example_procedure (\n    p_param1 IN VARCHAR2,\n    p_param2 IN NUMBER,\n    -- other parameters here\n    p_required_param1 IN VARCHAR2,\n    p_required_param2 IN VARCHAR2\n) AS\nBEGIN\n    IF p_required_param1 IS NULL THEN\n        RAISE_APPLICATION_ERROR(-20001, 'p_required_param1 cannot be null');\n    END IF;\n\n    IF p_required_param2 IS NULL THEN\n        RAISE_APPLICATION_ERROR(-20002, 'p_required_param2 cannot be null');\n    END IF;\n\n    -- Rest of the procedure logic here\n\nEND example_procedure;\n/