-- Finding the default constraint name for a specific column\nDECLARE @ConstraintName nvarchar(255)\nSELECT @ConstraintName = OBJECT_NAME(c.default_object_id) \nFROM sys.columns c \nINNER JOIN sys.tables t ON c.object_id = t.object_id \nWHERE t.name = 'TheTable' AND c.name = 'YourColumnName';\n\n-- Dropping the default constraint\nIF @ConstraintName IS NOT NULL\nBEGIN\n    EXEC ('ALTER TABLE TheTable DROP CONSTRAINT ' + QUOTENAME(@ConstraintName));\nEND