WITH WeekCounts AS (\n    SELECT \n        EXTRACT(WEEK FROM j.updated_at) as "week",  \n        count(j.id) as job_count\n    FROM  jobs j   \n    WHERE EXTRACT(YEAR FROM j.updated_at) = 2009   \n    GROUP BY EXTRACT(WEEK FROM j.updated_at)\n    ORDER BY week DESC -- Order by week in descending order to get the last weeks at the top\n    LIMIT 12 -- Limit to the top 12 in this reversed order, which are actually the bottom 12 originally\n)\nSELECT \n    *\nFROM WeekCounts\nORDER BY week ASC; -- Reorder the result set back to ascending order for sequential viewing