-- Create a table with some example dictionary words\nIF OBJECT_ID('DictionaryWords') IS NOT NULL DROP TABLE DictionaryWords;\nCREATE TABLE DictionaryWords (WordID INT IDENTITY(1,1), Word NVARCHAR(50));\n\nINSERT INTO DictionaryWords (Word)\nVALUES ('lorem'), ('ipsum'), ('dolor'), ('sit'), ('amet'), \n       ('consectetur'), ('adipiscing'), ('elit'), ('sed'), ('do'),\n       ('eiusmod'), ('tempor'), ('incididunt'), ('ut'), ('labore');\n\n-- Create a function to generate random words\nIF OBJECT_ID('udf_GetRandomWord') IS NOT NULL DROP FUNCTION udf_GetRandomWord;\nCREATE FUNCTION udf_GetRandomWord()\nRETURNS NVARCHAR(50)\nAS\nBEGIN\n    RETURN (SELECT TOP 1 Word FROM DictionaryWords ORDER BY NEWID());\nEND;\n\n-- Generate a random paragraph with n words\nDECLARE @Paragraph NVARCHAR(MAX) = '';\nDECLARE @WordCount INT = 20; -- Adjust the number of words per paragraph as needed\n\nWHILE @WordCount > 0\nBEGIN\n    SET @Paragraph += dbo.udf_GetRandomWord() + ' ';\n    SET @WordCount -= 1;\nEND;\n\nSET @Paragraph = RTRIM(@Paragraph) + '.';\n\n-- Insert the generated paragraph into your content table\nINSERT INTO YourContentTable (ColumnForParagraph)\nVALUES (@Paragraph);\n\nSELECT @Paragraph AS RandomParagraphGenerated;