CREATE OR REPLACE PACKAGE util_pkg AS\n  FUNCTION get_default_value(p_input IN NUMBER, p_default IN NUMBER) RETURN NUMBER;\nEND util_pkg;\n/\n\nCREATE OR REPLACE PACKAGE BODY util_pkg AS\n  FUNCTION get_default_value(p_input IN NUMBER, p_default IN NUMBER) RETURN NUMBER IS\n  BEGIN\n    IF p_input IS NULL THEN\n      RETURN p_default;\n    ELSE\n      RETURN p_input;\n    END IF;\n  END get_default_value;\nEND util_pkg;\n/