SELECT\n    c.id,\n    c.title,\n    SUM(CASE WHEN cls.section_id = 1 THEN 1 ELSE 0 END) AS doc1,\n    SUM(CASE WHEN cls.section_id = 2 THEN 1 ELSE 0 END) AS doc2,\n    SUM(CASE WHEN cls.section_id = 3 THEN 1 ELSE 0 END) AS doc3,\n    SUM(CASE WHEN cls.section_id = 4 THEN 1 ELSE 0 END) AS doc4\nFROM\n    category c\nLEFT JOIN\n    category_link_section cls ON c.id = cls.category_id\nGROUP BY\n    c.id, c.title;