CREATE TRIGGER update_symmetric_distance\nON Distances\nAFTER INSERT, UPDATE\nAS\nBEGIN\n    -- Assume all new/updated rows have equal distance for rowCol and colRow\n    INSERT INTO Distances (ColumnLocationID, RowLocationID, Distance)\n    SELECT inserted.ColumnLocationID, inserted.RowLocationID, inserted.Distance\n    FROM inserted\n    LEFT JOIN Distances d ON inserted.CoLoCID = d.RowLocationID AND inserted.RoCoID = d.ColumnLocationID\n    WHERE d.DISTANCE IS NULL;\nEND;