-- Table to store locations (both rows and columns)\nCREATE TABLE Locations (\n    LocationID INT PRIMARY KEY IDENTITY(1,1),\n    Name NVARCHAR(255) UNIQUE NOT NULL\n);\n\n-- Table to store distances between locations\nCREATE TABLE Distances (\n    RowLocationID INT,\n    ColumnLocationID INT,\n    Distance FLOAT,\n    PRIMARY KEY (RowLocationID, ColumnLocationID),\n    FOREIGN KEY (RowLocationID) REFERENCES Locations(LocationID),\n    FOREIGN KEY (ColumnLocationID) REFERENCES Locations(LocationID)\n);