CREATE OR REPLACE PROCEDURE GetPagedData (\n    p_page_number IN NUMBER,\n    p_rows_per_page IN NUMBER,\n    p_result OUT SYS_REFCURSOR\n) IS\nBEGIN\n    OPEN p_result FOR\n        SELECT *\n        FROM (\n            SELECT your_table.*, ROW_NUMBER() OVER (ORDER BY some_column) AS rn\n            FROM your_table\n        ) \n        WHERE rn > (p_page_number - 1) * p_rows_per_page\n          AND rn <= p_page_number * p_rows_per_page;\nEND GetPagedData;\n/