-- Example of a stored procedure in Oracle\nCREATE OR REPLACE PROCEDURE get_employee_salary(p_emp_id IN NUMBER, p_salary OUT NUMBER) AS\nBEGIN\n  SELECT salary INTO p_salary FROM employees WHERE employee_id = p_emp_id;\nEND;\n\n-- Example of executing dynamic SQL\nDECLARE\n   v_sql VARCHAR2(100);\n   v_salary NUMBER;\n   v_emp_id NUMBER := 10; -- example employee ID\nBEGIN\n   v_sql := 'SELECT salary FROM employees WHERE employee_id = :empid';\n   EXECUTE IMMEDIATE v_sql INTO v_salary USING v_emp_id;\nEND;\n\n-- Performance testing can be done using SET AUTOTRACE, or by using Oracle's SQL Developer or PL/SQL Developer tools.