SET NOCOUNT ON;\nUSE msdb;\nGO\n\nDECLARE @job_name SYSNAME;\n\nDECLARE JobCursor CURSOR FOR \nSELECT name FROM dbo.sysjobs ORDER BY name;\n\nOPEN JobCursor;\n\nFETCH NEXT FROM JobCursor INTO @job_name;\n\nWHILE @@FETCH_STATUS = 0\nBEGIN\n    PRINT '-- Script for job: ' + QUOTENAME(@job_name);\n    EXEC sp_help_job @job_name=@job_name, @format_output=1;\n    \n    FETCH NEXT FROM JobCursor INTO @job_name;\nEND\n\nCLOSE JobCursor;\nDEALLOCATE JobCursor;