-- Table for meta data that rarely changes\nCREATE TABLE question_meta (\n    question_id INT PRIMARY KEY,\n    title VARCHAR(255) NOT NULL,\n    content TEXT NOT NULL,\n    created_at TIMESTAMP NOT NULL,\n    updated_at TIMESTAMP NOT NULL\n);\n\n-- Table for frequently changing data\nCREATE TABLE question_stats (\n    question_id INT PRIMARY KEY,\n    total_views INT DEFAULT 0 NOT NULL,\n    last_viewed_date TIMESTAMP NULL,\n    FOREIGN KEY (question_id) REFERENCES question_meta(question_id)\n);