DECLARE @StartRow INT, @EndRow INT, @PageSize INT = 1000, @PageNumber INT = 1\n\nSET @StartRow = (@PageNumber - 1) * @PageSize + 1\nSET @EndRow   = @StartRow + @PageSize - 1\n\nSELECT *\nFROM (\n    SELECT \n        ROW_NUMBER() OVER (ORDER BY YourColumn) AS RowNum,\n        -- Specify the columns you need here\n        Column1, Column2, ...\n    FROM YourTable\n    WHERE YourCondition\n) SubQuery\nWHERE RowNum BETWEEN @StartRow AND @EndRow;