-- Parent table\nCREATE TABLE Person (\n    person_id INT PRIMARY KEY,\n    name VARCHAR(100),\n    -- other common attributes\n);\n\n-- Child tables for each subclass\nCREATE TABLE Student (\n    student_id INT PRIMARY KEY REFERENCES Person(person_id),\n    student_number VARCHAR(20),\n    -- other specific attributes for students\n);\n\nCREATE TABLE Employee (\n    employee_id INT PRIMARY KEY REFERENCES Person(person_id),\n    employee_code VARCHAR(15),\n    -- other specific attributes for employees\n);