CREATE TABLE TeamMemberships (\n  player_id int NOT NULL UNIQUE REFERENCES Players(player_id),\n  team_id int NOT NULL,\n  PositionInTeam INT NOT NULL CHECK (PositionInTeam IN (0,1)),\n  PRIMARY KEY (team_id, PositionInTeam),\n  FOREIGN KEY (team_id) REFERENCES Teams(team_id)\n);\n\n-- Example of a trigger approach if needed\nCREATE OR REPLACE FUNCTION enforce_team_size() RETURNS TRIGGER AS $$\nBEGIN\n    IF (SELECT COUNT(*) FROM TeamMemberships WHERE team_id = NEW.team_id) > 2 THEN\n        RAISE EXCEPTION 'Team can have at most two players';\n    END IF;\n    RETURN NEW;\nEND;\n$$ LANGUAGE plpgsql;\n\nCREATE TRIGGER check_team_size BEFORE INSERT OR UPDATE ON TeamMemberships FOR EACH ROW EXECUTE FUNCTION enforce_team_size();