DECLARE @SourcePrincipal sysname = 'SourceRole'; -- Replace with the original role/user\nDECLARE @TargetPrincipal sysname = 'TargetRole'; -- Replace with the new role/user\n\nDECLARE @PermissionSQL NVARCHAR(MAX) '');\n\nSELECT @PermissionSQL = @PermissionSQL + \n    'GRANT ' + dp.permission_name collate SQL_Latin1_General_CP1_CI_AS +\n    ' ON [' + s.name + '].[' + o.name + '] TO ' + QUOTENAME(@TargetPrincipal) + ';' + CHAR(13)\nFROM sys.database_permissions AS dp\nJOIN sys.objects AS o ON dp.major_id = o.object_id\nJOIN sys.schemas AS s ON o.schema_id = s.schema_id\nWHERE dp.grantee_principal_id  = USER_ID(@SourcePrincipal);\n\n-- Review the script before execution.\nPRINT @PermissionSQL;\n\n-- Uncomment to execute the script.\n-- EXEC sp_executesql @PermissionSQL;