CREATE TABLE PAYMENT (\n    id INT PRIMARY KEY,\n    -- other payment columns\n);\n\nCREATE TABLE TIMESTAMP_RECORDS (\n    table_name VARCHAR(255) NOT NULL,\n    record_id INT NOT NULL,\n    timestamp_type ENUM('insert', 'update', 'delete') NOT NULL,  -- or use an INT with a mapping if preferred\n    action_timestamp DATETIME DEFAULT CURRENT_TIMESTAMP,\n    PRIMARY KEY (table_name, record_id, action_timestamp)\n);