CREATE PROCEDURE dbo.GetCommonData\nAS\nBEGIN TRY\n    IF OBJECT_ID('tempdb..#TempTable1') IS NOT NULL \n        DROP TABLE #TempTable1;\n    \n    IF OBJECT_ID('tempdb..#TempTable2') IS NOT NULL \n        DROP TABLE #TempTable2;\n\n    CREATE TABLE #TempTable1 (\n        ID INT,\n        Column1 VARCHAR(100)\n        -- Define other columns\n    );\n\n    CREATE TABLE #TempTable2 (\n        ID INT,\n        Column2 INT\n        -- Define other columns\n    );\n\n    -- Insert data into temp tables\n    INSERT INTO #TempTable1 (ID, Column1) VALUES (1, 'Data1');\n    INSERT INTO #TempTable1 (ID, Column1) VALUES (2, 'Data2');\n\n    INSERT INTO #TempTable2 (ID, Column2) VALUES (1, 10);\n    INSERT INTO #TempTable2 (ID, Column2) VALUES (2, 20);\n\n    SELECT * FROM #TempTable1;\n    SELECT * FROM #TempTable2;\n\nEND TRY\nBEGIN CATCH\n    -- Error handling code here\n    IF OBJECT_ID('tempdb..#TempTable1') IS NOT NULL \n        DROP TABLE #TempTable1;\n    \n    IF OBJECT_ID('tempdb..#TempTable2') IS NOT NULL \n        DROP TABLE #TempTable2;\n\n    RAISERROR ('An error occurred: %s', 16, 1, ERROR_MESSAGE());\nEND CATCH