SELECT b.profname\nFROM committee b\nWHERE NOT EXISTS (\n    SELECT commname\n    FROM committee c\n    WHERE c.profname = 'piper'\n      AND NOT EXISTS (\n          SELECT 1\n          FROM committee d\n          WHERE d.profname = b.profname\n            AND d.commname = c.commname\n      )\n)\nGROUP BY b.profname\nHAVING COUNT(DISTINCT b.commname) = (\n    SELECT COUNT(DISTINCT commname)\n    FROM committee\n    WHERE profname = 'piper'\n);