WITH RankedLogs AS (\n    SELECT\n        id,\n        Name,\n        Status,\n        ROW_NUMBER() OVER (PARTITION BY Name ORDER BY \n            CASE WHEN Status = 'Closed' THEN 1\n                 WHEN Status = 'Hold' THEN 2\n                 WHEN Status = 'Waiting' THEN 3\n                 WHEN Status = 'Open' THEN 4\n                 ELSE 5 -- Optional: handle unexpected statuses\n            END) as rn\n    FROM your_logging_table\n)\nSELECT id, Name, Status\nFROM RankedLogs\nWHERE rn = 1;