DELIMITER //\n\nCREATE TRIGGER ResetIndexAtYearEnd\nAFTER INSERT ON your_table_name -- Change this to your actual table name\nFOR EACH ROW\nBEGIN\n    DECLARE CurrentDate DATE;\n    SET CurrentDate = CURDATE();\n\n    IF MONTH(CurrentDate) = 1 AND DAY(CurrentDate) = 1 THEN\n        -- This part is pseudo-code, MySQL doesn't reset auto_increment in a trigger\n        -- Instead, you should handle this with an external script or scheduled event\n        -- But for illustration purposes:\n        \n        SET GLOBAL sql_mode=(SELECT REPLACE(@@sql_mode,'NO_AUTO_VALUE_ON_ZERO',''));\n        \n        ALTER TABLE your_table_name AUTO_INCREMENT = 1;\n    END IF;\nEND; //\n\nDELIMITER ;