CREATE TRIGGER PreventMasterDeletion ON YourTableName\nINSTEAD OF DELETE\nAS\nBEGIN\n    -- Block deletion if the ID is 0, 1 or 2\n    IF EXISTS (SELECT 1 FROM deleted WHERE id IN (0, 1, 2))\n    BEGIN\n        ROLLBACK TRANSACTION;\n        RAISERROR ('Cannot delete master accounts with ID 0, 1, or 2.', 16, 1);\n    END\n    ELSE\n    BEGIN\n        -- Proceed with the deletion for other IDs\n        DELETE FROM YourTableName\n        WHERE id NOT IN (SELECT id FROM deleted WHERE id IN (0, 1, 2))\n    END\nEND;