CREATE VIEW dbo.InvoiceByCustomerView AS\nSELECT * FROM Invoice WHERE CustomerID = @customerID; -- This will need dynamic SQL or another approach for parameters\n\n-- Then use this view in stored procedures:\nCREATE PROCEDURE hd_invoice_selectAllForCustomer(@customerID INT) AS\nBEGIN\n    DECLARE @query NVARCHAR(MAX);\n    SET @query = N'SELECT * FROM InvoiceByCustomerView WHERE CustomerID = ' + CAST(@customerID AS NVARCHAR(10));\n    EXEC sp_executesql @query;\n    \n    -- Similar for items and comments with proper filtering logic\nEND