CREATE PROCEDURE hd_invoice_select_main(@filter NVARCHAR(MAX)) AS\nBEGIN\n    EXEC sp_executesql @filter;\nEND\n\n-- Similarly for items and comments:\nCREATE PROCEDURE hd_invoice_select_items(@filter NVARCHAR(MAX)) AS\nBEGIN\n    EXEC sp_executesql @filter;\nEND\n\n-- Usage in a main proc:\nCREATE PROCEDURE hd_invoice_selectAllForCustomer(@customerID INT) AS\nBEGIN\n    DECLARE @mainFilter NVARCHAR(MAX);\n    SET @mainFilter = N'SELECT * FROM Invoice WHERE CustomerID = ' + CAST(@customerID AS NVARCHAR(10));\n    EXEC hd_invoice_select_main @mainFilter;\n    \n    -- Repeat for items and comments with correct filtering logic\nEND