BEGIN TRANSACTION\n   TRY \n       INSERT INTO dbo.ITSUsage (\n           Customer_ID,\n           [Type],\n           Source\n       ) VALUES ( \n           @Customer_ID,\n           @Type,\n           @Source\n        )\n       SET @ID = SCOPE_IDENTITY();  \n    \n       IF @Type = 1\n       BEGIN\n          exec usp_Type1_INS @ID, @UsageInfo; \n       END\n       ELSE IF @TYPE = 2\n       BEGIN\n          exec usp_Type2_INS @ID, @UsageInfo;\n       END\n\n       COMMIT TRANSACTION;\n   END TRY\n   BEGIN CATCH\n       ROLLBACK TRANSACTION;\n       -- Handle error (e.g., log it or rethrow)\n       THROW;      \n   END CATCH;