DECLARE\n    v_blob_locator BLOB;\n    v_buffer RAW(32767);\n    v_buffer_size NUMBER := 8192; -- Chunk size\n    v_offset NUMBER := 1; -- Start position\n    v_amount NUMBER;\n    v_pattern RAW(32) := HEXTORAW('yourpatternhere'); -- Pattern to search in HEX\n    v_match_start NUMBER;\nBEGIN\n    SELECT your_blob_column INTO v_blob_locator FROM your_table WHERE your_condition FOR UPDATE;\n\n    LOOP\n        DBMS_LOB.READ(v_blob_locator, v_buffer_size, v_offset, v_buffer);\n\n        -- Search for pattern in the buffer\n        v_match_start := INSTR(v_buffer, v_pattern);\n        IF v_match_start > 0 THEN\n            DBMS_OUTPUT.PUT_LINE('Pattern found at position: ' || (v_offset + v_match_start - 1));\n            EXIT;\n        END IF;\n\n        v_offset := v_offset + v_buffer_size;\n    EXIT WHEN v_amount < v_buffer_size; -- Exit when end of BLOB is reached\n    END LOOP;\n\nEXCEPTION\n    WHEN NO_DATA_FOUND THEN\n        DBMS_OUTPUT.PUT_LINE('Pattern not found.');\nEND;