WITH RecursiveCustomers AS (\n    -- Anchor member: Select James and his ID\n    SELECT CustomerID, ParentID, Name\n    FROM Customers\n    WHERE Name = 'James'\n\n    UNION ALL\n\n    -- Recursive member: Select children of the current selection\n    SELECT c.CustomerID, c.ParentID, c.Name\n    FROM Customers c\n    INNER JOIN RecursiveCustomers rc ON c.ParentID = rc.CustomerID\n)\nSELECT CustomerID, ParentID, Name\nFROM RecursiveCustomers\nWHERE Name <> 'James'; -- Exclude James from the final result if you only want his descendants