-- Create table in new database with the same schema as the original one\n   SELECT * INTO LargeTableDB.dbo.YourLargeTableName\n   FROM YourOriginalDatabaseName.dbo.YourLargeTableName\n   WHERE 1 = 0; -- this condition ensures that no rows are copied, just the structure\n\n   -- Transfer the data\n   INSERT INTO LargeTableDB.dbo.YourLargeTableName\n   SELECT * FROM YourOriginalDatabaseName.dbo.YourLargeTableName;