-- On Primary Server: Backup the database and transaction logs\nBACKUP DATABASE [MyDB] TO DISK = N'\\FileServer\Logshipping\MyDB.bak'\nWITH NOFORMAT, INIT, NAME = 'MyDB-Full Database Backup', SKIP, NOREWIND, NOUNLOAD, STATS = 10\n\nBACKUP LOG [MyDB] TO DISK = N'\\FileServer\Logshipping\MyDB.trn'\nWITH NOFORMAT, INIT, NAME = 'MyDB-Transaction Log Backup', SKIP, NOREWIND, NOUNLOAD, STATS = 10\n\n-- On Secondary Server: Restore the backup with NORECOVERY\nRESTORE DATABASE [MyDB] \nFROM DISK = N'\\FileServer\Logshipping\MyDB.bak'\nWITH FILE = 1,\nMOVE N'MyDB_Data' TO N'C:\SQLData\MyDB.mdf',  \nMOVE N'MyDB_Log' TO N'C:\SQLLog\MyDB_log.ldf',  \nNORECOVERY, STATS = 10\n\n-- On Secondary Server: Restore the transaction log with NORECOVERY\nRESTORE LOG [MyDB] \nFROM DISK = N'\\FileServer\Logshipping\MyDB.trn'\nWITH FILE = 1,\nNORECOVERY, STATS = 10