CREATE TABLE team_mode_rounds (\n    replay_id INT UNSIGNED NOT NULL,\n    round SMALLINT UNSIGNED NOT NULL,\n    winning_team_id TINYINT UNSIGNED NOT NULL,\n    FOREIGN KEY (replay_id) REFERENCES replays(id),\n    FOREIGN KEY (winning_team_id) REFERENCES teams(id),\n    PRIMARY KEY (replay_id, round)\n);\n\nCREATE TABLE individual_mode_rounds (\n    replay_id INT UNSIGNED NOT NULL,\n    round SMALLINT UNSIGNED NOT NULL,\n    winning_player_id INT UNSIGNED NOT NULL,\n    FOREIGN KEY (replay_id) REFERENCES replays(id),\n    FOREIGN KEY (winning_player_id) REFERENCES players(id),\n    PRIMARY KEY (replay_id, round)\n);