-- Assuming there's a TimezoneOffset column where you store the offset, e.g., '-04:00' for EDT and '-05:00' for EST\n\nUPDATE YourTable\nSET DateTimeUTC = CASE \n                    WHEN CONVERT(INT, RIGHT(CONVERT(VARCHAR(8), SWITCHOFFSET(CAST(DateTimeLocal AS DATETIMEOFFSET), TimezoneOffset), 2)) <= 10 THEN  \n                        -- Assume Eastern Daylight Time (EDT) if offset is -04:00\n                        DATEADD(HOUR, 4, DateTimeLocal)\n                    ELSE \n                        -- Assume Eastern Standard Time (EST) if offset is -05:00\n                        DATEADD(HOUR, 5, DateTimeLocal)\n                  END;