SELECT \n    CASE DATEPART(dw, [Date]) -- Get the day of the week number (1=Sunday, ..., 7=Saturday)\n        WHEN 2 THEN DATEADD(day, 1, [Date]) -- Monday\n        WHEN 3 THEN DATEADD(day, 1, [Date]) -- Tuesday\n        WHEN 4 THEN DATEADD(day, 1, [Date]) -- Wednesday\n        WHEN 5 THEN DATEADD(day, 1, [Date]) -- Thursday\n        WHEN 6 THEN DATEADD(day, 3, [Date]) -- Friday\n        ELSE [Date] -- For Saturday and Sunday, no change\n    END AS TransformedDate\nFROM myTable;