CREATE TABLE user_infos (\n    user_id SERIAL PRIMARY KEY,\n    username VARCHAR(25),\n    email VARCHAR(320) NOT NULL UNIQUE,\n    password_sha512 TEXT NOT NULL,\n    is_moderator BOOLEAN DEFAULT FALSE NOT NULL,\n    is_login BOOLEAN DEFAULT FALSE NOT NULL,\n    has_been_sent_a_moderator_message BOOLEAN DEFAULT FALSE NOT NULL\n);\n\nCREATE TABLE questions (\n    question_id SERIAL PRIMARY KEY,\n    user_id INTEGER REFERENCES user_infos(user_id) NOT NULL CHECK (user_id > 0),\n    body TEXT NOT NULL,\n    title VARCHAR(60) NOT NULL,\n    moderator_removal BOOLEAN DEFAULT FALSE NOT NULL,\n    sent_time TIMESTAMP NOT NULL\n);\n\nCREATE TABLE answers (\n    answer_id SERIAL PRIMARY KEY, \n    question_id INTEGER REFERENCES questions(question_id) ON DELETE CASCADE NOT NULL CHECK (question_id > 0),\n    answer TEXT NOT NULL\n);\n\nCREATE TABLE tags (\n    tag_id SERIAL PRIMARY KEY,\n    question_id INTEGER REFERENCES questions(question_id) ON DELETE CASCADE NOT NULL,\n    tag VARCHAR(20) NOT NULL,\n    UNIQUE (question_id, tag)\n);