CREATE TRIGGER insert_import_table_trigger\n   ON import_table\n   INSTEAD OF INSERT\n   AS\n   BEGIN\n       SET NOCOUNT ON;\n\n       IF EXISTS (\n           SELECT 1 \n           FROM inserted i\n           LEFT JOIN inventory_master im ON i.part_number = im.part_number AND im.type = 'C'\n           WHERE im.part_number IS NULL\n       )\n       BEGIN\n           RAISERROR ('Invalid part number or type. Part numbers must exist in inventory_master with type ''C''.', 16, 1);\n           ROLLBACK TRANSACTION;\n           RETURN;\n       END;\n\n       INSERT INTO import_table (part_number, quantity)\n       SELECT part_number, quantity FROM inserted;\n   END;