-- Set your primary and secondary user IDs\nSET @primary_user_id = 1; -- Replace with the actual primary user ID\nSET @secondary_user_id = 2; -- Replace with the actual secondary user ID\n\n-- Update lessonstatus_rtab to redirect activities from the secondary user to the primary user\nUPDATE lessonstatus_rtab\nSET user_id = @primary_user_id\nWHERE user_id = @secondary_user_id;\n\n-- Optionally, update other related tables if necessary (not included in this example)\n\n-- Delete the secondary user from user_rtab\nDELETE FROM user_rtab WHERE user_id = @secondary_user_id;