DECLARE @Year INT = 2009;\nDECLARE @WeekNumber INT = 1;\n\nSELECT \n    DATEADD(WEEK, DATEDIFF(WEEK, 0, DATEADD(DAY, 6 - (DATEPART(WEEKDAY, '2009-01-01') + @@DATEFIRST) % 7, '2009-01-01')) + @WeekNumber - 1, 0) AS StartDate,\n    DATEADD(WEEK, DATEDIFF(WEEK, 0, DATEADD(DAY, 6 - (DATEPART(WEEKDAY, '2009-01-01') + @@DATEFIRST) % 7, '2009-01-01')) + @WeekNumber, -1) AS EndDate;