CREATE TABLE Skill (\n    SkillId INT PRIMARY KEY,\n    SkillName VARCHAR(255) NOT NULL\n);\n\nCREATE TABLE SkillRelationship (\n    SourceSkillId INT,\n    TargetSkillId INT,\n    RelevanceLevel FLOAT CHECK (RelevanceLevel BETWEEN 0 AND 1),\n    RelationType ENUM('explicit', 'implicit'),\n    PRIMARY KEY (SourceSkillId, TargetSkillId),\n    FOREIGN KEY (SourceSkillId) REFERENCES Skill(SkillId),\n    FOREIGN KEY (TargetSkillId) REFERENCES Skill(SkillId)\n);