-- Create MemberProfile table\nCREATE TABLE MemberProfile (\n    MemberID INT PRIMARY KEY,\n    FirstName NVARCHAR(50),\n    LastName NVARCHAR(50),\n    Email NVARCHAR(100)\n);\n\n-- Create MemberAccount table\nCREATE TABLE MemberAccount (\n    MemberID INT PRIMARY KEY,\n    AccountNumber NVARCHAR(20),\n    Balance DECIMAL(18, 2),\n    FOREIGN KEY (MemberID) REFERENCES MemberProfile(MemberID)\n);\n\n-- Create MemberPreferences table\nCREATE TABLE MemberPreferences (\n    MemberID INT PRIMARY KEY,\n    NotificationsEnabled BIT,\n    ThemePreference NVARCHAR(50),\n    FOREIGN KEY (MemberID) REFERENCES MemberProfile(MemberID)\n);