CREATE OR REPLACE PROCEDURE usp_dropTable(schema VARCHAR, tblToDrop VARCHAR) \nAUTHID CURRENT_USER IS\n  v_cnt NUMBER;\nBEGIN\n  SELECT COUNT(*) \n    INTO v_cnt \n    FROM all_tables \n   WHERE owner = schema\n     AND table_name = UPPER(tblToDrop); -- Ensure the table name is in uppercase\n\n  IF v_cnt > 0 THEN \n     EXECUTE IMMEDIATE 'DROP TABLE ' || schema || '.' || tblToDrop || ' PURGE';\n  END IF;\nEND;